import React, { useState } from "react";
import instance from "../../../utils/AxiosConfig";
import "../../../styles/frndPop.css";

export default function SettleUp({ onClose }) {
  const [id, setId] = useState("");
  const [msg, setMsg] = useState("");
  const [loading, setLoading] = useState(false);

  const settle = async () => {
    setMsg("");
    setLoading(true);
    try {
      await instance.post("/user/expense/settle", { id });
      setMsg("✅ Settled");
      setId("");
    } catch (e) {
      setMsg(e?.response?.data?.msg || "❌ Error settling");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal">
      <div className="modal-card glass">
        <h3>Settle Up</h3>
        <p className="mini">Enter the Expense ID to mark it as settled.</p>
        <input
          placeholder="Expense ID"
          value={id}
          onChange={(e) => setId(e.target.value)}
        />
        <div className="modal-actions">
          <button className="btn" onClick={settle} disabled={loading}>
            {loading ? "Settling…" : "Settle"}
          </button>
          <button className="btn outline" onClick={onClose}>
            Close
          </button>
        </div>
        {msg && (
          <p className="mini" style={{ marginTop: 8 }}>
            {msg}
          </p>
        )}
      </div>
    </div>
  );
}
