import React, { useState } from "react";
import { instance } from "../utils/AxiosConfig";
import handleApiError from "../utils/handleApiError";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [msg, setMsg] = useState("");

  const onLogin = async () => {
    setMsg("");
    try {
      const res = await instance.post("/login", { email, password });
      setMsg(`Welcome ${res.data.user.username}`);
      // store token if you like:
      // setAuthorizationToken(res.data.token);
    } catch (err) {
      handleApiError(err, setMsg);
    }
  };

  return (
    <div
      style={{ maxWidth: 420, margin: "60px auto", fontFamily: "system-ui" }}
    >
      <h2>Login</h2>
      {msg && (
        <div
          style={{
            padding: 10,
            background: "#fff3cd",
            borderRadius: 8,
            marginBottom: 12,
          }}
        >
          {msg}
        </div>
      )}
      <label>Email</label>
      <input
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        className="form-control"
        style={{
          width: "100%",
          padding: 10,
          margin: "6px 0 12px",
          borderRadius: 8,
          border: "1px solid #ccc",
        }}
      />
      <label>Password</label>
      <input
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        className="form-control"
        style={{
          width: "100%",
          padding: 10,
          margin: "6px 0 12px",
          borderRadius: 8,
          border: "1px solid #ccc",
        }}
      />
      <button
        onClick={onLogin}
        style={{
          padding: "10px 16px",
          borderRadius: 8,
          background: "#4f46e5",
          color: "#fff",
          border: 0,
        }}
      >
        Sign in
      </button>
    </div>
  );
}
