import React, { useState } from "react";
import instance from "../../../utils/AxiosConfig";
import "../../../styles/frndPop.css";

export default function AddExpense({ onClose }) {
  const [form, setForm] = useState({
    description: "",
    amount: "",
    paidBy: "",
    paidTo: "",
  });
  const [msg, setMsg] = useState("");

  const onChange = (e) => setForm({ ...form, [e.target.id]: e.target.value });

  const add = async () => {
    try {
      await instance.post("/user/expense/add", form);
      setMsg("Expense added!");
    } catch {
      setMsg("Error adding expense");
    }
  };

  return (
    <div className="modal">
      <div className="modal-card">
        <h3>Add Expense</h3>
        <input id="description" placeholder="Description" onChange={onChange} />
        <input
          id="amount"
          placeholder="Amount"
          type="number"
          onChange={onChange}
        />
        <input id="paidBy" placeholder="Paid by (email)" onChange={onChange} />
        <input id="paidTo" placeholder="Paid to (email)" onChange={onChange} />
        <div className="modal-actions">
          <button onClick={add}>Add</button>
          <button className="btn-outline" onClick={onClose}>
            Close
          </button>
        </div>
        {msg && <p>{msg}</p>}
      </div>
    </div>
  );
}
