// src/redux/reducers/userReducer.js

const initialState = {
  user: {
    _id: null,
    username: "",
    email: "",
    friends: [],
    expensis: [], // your app originally used "expensis"
    // if your API uses "expenses", we’ll normalize below
  },
};

export const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case "AddUser": {
      const doc = action.payload || {};
      // some endpoints might send { user: { ... } }
      const incoming =
        doc.user && (doc.user.username || doc.user.expensis || doc.user.friends)
          ? doc.user
          : doc;

      return {
        user: {
          ...state.user,
          ...incoming,
          friends: Array.isArray(incoming.friends)
            ? incoming.friends
            : state.user.friends || [],
          // normalize both "expensis" and "expenses"
          expensis: Array.isArray(incoming.expensis)
            ? incoming.expensis
            : Array.isArray(incoming.expenses)
            ? incoming.expenses
            : state.user.expensis || [],
        },
      };
    }

    // append one or many expenses (normalized)
    case "APPEND_EXPENSES": {
      const add = Array.isArray(action.payload)
        ? action.payload
        : [action.payload];
      return {
        user: {
          ...state.user,
          expensis: [...(state.user.expensis || []), ...add],
        },
      };
    }

    case "LOGOUT":
      return initialState;

    default:
      return state;
  }
};
