import React from "react";
import { formatCurrency } from "../../utils/finance";
import "../../styles/dashboard.css";

// Minimal, dependency-free horizontal bar chart using SVG.
export default function CategoryChart({
  data = [],
  total = 0,
  title = "Monthly Spend",
}) {
  const max = data.length ? Math.max(...data.map((d) => d.value)) : 0;
  const height = Math.max(data.length * 34 + 20, 80);
  const padLeft = 120;
  const padRight = 24;

  return (
    <div className="panel glass">
      <div className="panel-head">
        <h3>{title}</h3>
        <div className="mini">
          {total ? `Total: ${formatCurrency(total)}` : "No data"}
        </div>
      </div>

      {data.length === 0 ? (
        <div className="mini">
          Add expenses with categories to see this chart.
        </div>
      ) : (
        <svg
          className="hbar"
          width="100%"
          height={height}
          viewBox={`0 0 600 ${height}`}
          preserveAspectRatio="none"
        >
          {data.map((d, i) => {
            const y = 10 + i * 34;
            const w = max ? (d.value / max) * (600 - padLeft - padRight) : 0;
            return (
              <g key={d.label} transform={`translate(0, ${y})`}>
                <text x={8} y={16} className="hbar-label">
                  {d.label}
                </text>
                <rect
                  x={padLeft}
                  y={4}
                  width={w}
                  height={18}
                  className="hbar-bar"
                  rx="9"
                />
                <text x={padLeft + w + 8} y={18} className="hbar-value">
                  {formatCurrency(d.value)}
                </text>
              </g>
            );
          })}
        </svg>
      )}
    </div>
  );
}
