import React, { useMemo, useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import AddExpense from "./popups/addExpense";
import Friend from "./popups/Friend";
import "../../styles/dashboard.css";

import { instance } from "../../utils/AxiosConfig";
import { userActionCreator } from "../../redux/actionCreator/userAction";

/* ---------- Helpers ---------- */
const asArray = (v) => (Array.isArray(v) ? v : []);
const toNumber = (v) => {
  const n = parseFloat(v);
  return isNaN(n) ? 0 : n;
};

function pick(obj, paths) {
  for (let p of paths) {
    const val = p
      .split(".")
      .reduce((o, k) => (o && o[k] !== undefined ? o[k] : undefined), obj);
    if (val !== undefined && val !== null && val !== "") return val;
  }
  return undefined;
}
function formatDate(iso) {
  const d = iso ? new Date(iso) : null;
  if (!d || isNaN(d.getTime())) return "‚Äî";
  return d.toLocaleDateString(undefined, {
    day: "2-digit",
    month: "short",
    year: "numeric",
  });
}
const getDesc = (e) =>
  pick(e, ["description", "title", "note", "inp.description"]) || "Untitled";
const getAmt = (e) => toNumber(pick(e, ["amount", "inp.amount", "value"]));
const getDateVal = (e) =>
  pick(e, ["date", "createdAt", "updatedAt", "inp.date"]) || null;
const getWho = (e) => pick(e, ["user", "friend", "paidBy", "with"]);
const getId = (e) => (e && (e._id || e.id)) || null;

function sig(e) {
  return `${getDesc(e)}|${getAmt(e)}|${getDateVal(e) || ""}|${getWho(e) || ""}`;
}

/* ---------- Robust server delete (tries multiple routes/payloads/methods) ---------- */
async function tryDeleteOnServer({ username, id, who, desc, amount, dateISO }) {
  const u = username || localStorage.username;

  // Candidate endpoints (we‚Äôll try each with several payloads & methods)
  const endpoints = [
    "/deleteExp",
    "/deleteExpense",
    "/removeExp",
    "/removeExpense",
    "/expenses/delete",
    "/expense/delete",
    "/delete", // very generic backends
  ];

  // Payload candidates (most specific first)
  const base = { username: u };
  const bodyCandidates = [];

  if (id) {
    bodyCandidates.push({ ...base, id });
    bodyCandidates.push({ ...base, expenseId: id });
    bodyCandidates.push({ ...base, _id: id });
  }

  // Field-match style (no id)
  bodyCandidates.push({
    ...base,
    user: who || null,
    inp: { description: desc, amount, date: dateISO },
  });
  bodyCandidates.push({
    ...base,
    user: who || null,
    description: desc,
    amount,
    date: dateISO,
  });

  // When id exists, also try path-param and REST-y patterns
  const pathCandidates = id
    ? [
        `/deleteExp/${encodeURIComponent(id)}`,
        `/deleteExpense/${encodeURIComponent(id)}`,
        `/removeExp/${encodeURIComponent(id)}`,
        `/removeExpense/${encodeURIComponent(id)}`,
        `/expenses/${encodeURIComponent(id)}`,
        `/expense/${encodeURIComponent(id)}`,
      ]
    : [];

  // GET/DELETE with querystring fallback
  const qs = id
    ? `?id=${encodeURIComponent(id)}&username=${encodeURIComponent(u)}`
    : `?username=${encodeURIComponent(u)}&user=${encodeURIComponent(
        who || ""
      )}&description=${encodeURIComponent(desc)}&amount=${encodeURIComponent(
        amount
      )}&date=${encodeURIComponent(dateISO || "")}`;

  // Success test (tolerant)
  const ok = (resp) => {
    if (!resp) return false;
    if (resp.status === 204) return true; // No Content considered success
    if (resp.status >= 200 && resp.status < 300 && !resp.data) return true;
    const d = resp.data || {};
    return (
      resp.status >= 200 &&
      resp.status < 300 &&
      (d.success === true ||
        d.deleted === true ||
        d.ok === 1 ||
        d.acknowledged === true ||
        d.status === "ok" ||
        (typeof d.deletedCount === "number" && d.deletedCount > 0) ||
        d.removed === true)
    );
  };

  const attempts = [];

  // 1) POST to endpoints with each body
  for (const url of endpoints) {
    for (const b of bodyCandidates) {
      try {
        // eslint-disable-next-line no-await-in-loop
        const resp = await instance.post(url, b);
        if (ok(resp)) return { ok: true, via: `POST ${url}`, body: b, resp };
        attempts.push({ via: `POST ${url}`, body: b, data: resp?.data });
      } catch (e) {
        attempts.push({ via: `POST ${url}`, body: b, error: e?.message });
      }
    }
  }

  // 2) DELETE with body (axios supports { data })
  for (const url of endpoints) {
    for (const b of bodyCandidates) {
      try {
        // eslint-disable-next-line no-await-in-loop
        const resp = await instance.delete(url, { data: b });
        if (ok(resp)) return { ok: true, via: `DELETE ${url}`, body: b, resp };
        attempts.push({ via: `DELETE ${url}`, body: b, data: resp?.data });
      } catch (e) {
        attempts.push({ via: `DELETE ${url}`, body: b, error: e?.message });
      }
    }
  }

  // 3) DELETE with path id
  for (const url of pathCandidates) {
    try {
      // eslint-disable-next-line no-await-in-loop
      const resp = await instance.delete(url, { data: base });
      if (ok(resp)) return { ok: true, via: `DELETE ${url}`, body: base, resp };
      attempts.push({ via: `DELETE ${url}`, body: base, data: resp?.data });
    } catch (e) {
      attempts.push({ via: `DELETE ${url}`, body: base, error: e?.message });
    }
  }

  // 4) GET with querystring (last resort)
  for (const url of endpoints) {
    try {
      // eslint-disable-next-line no-await-in-loop
      const resp = await instance.get(url + qs);
      if (ok(resp)) return { ok: true, via: `GET ${url}${qs}`, resp };
      attempts.push({ via: `GET ${url}${qs}`, data: resp?.data });
    } catch (e) {
      attempts.push({ via: `GET ${url}${qs}`, error: e?.message });
    }
  }

  // Log a compact table so you can see exactly what the server wants
  if (console.table) console.table(attempts);
  else console.log("Delete attempts:", attempts);

  return { ok: false, attempts };
}

/* ---------- Row ---------- */
function ExpenseRow({ item, onDelete, deleting }) {
  const desc = getDesc(item);
  const amt = getAmt(item);
  const dt = getDateVal(item);
  const who = getWho(item);

  return (
    <div className="exp-row">
      <div className="exp-main">
        <div className="exp-desc">{desc}</div>
        <div className="exp-meta">
          <span className="pill">{formatDate(dt)}</span>
          {who ? (
            <span className="pill pill-ghost">@ {String(who)}</span>
          ) : null}
        </div>
      </div>

      <div className="exp-right">
        <div className="exp-amount">‚Çπ{amt.toFixed(2)}</div>
        <button
          className="btn-delete"
          title="Delete expense"
          onClick={() => onDelete(item)}
          disabled={deleting}
        >
          {deleting ? (
            <span className="spinner tiny" />
          ) : (
            <i className="fas fa-trash" />
          )}
        </button>
      </div>
    </div>
  );
}

/* ---------- Dashboard ---------- */
export default function Dashboard() {
  const dispatch = useDispatch();

  // Works with both shapes:
  const userState = useSelector((state) => (state && state.user) || {});
  const userDoc =
    userState && userState._id !== undefined ? userState : userState.user || {};

  const username =
    (userDoc && userDoc.username) || localStorage.username || "User";
  const friends = asArray(userDoc && userDoc.friends);
  const expensesRaw = asArray(userDoc && userDoc.expensis);

  const [showAddExpense, setShowAddExpense] = useState(false);
  const [showAddFriend, setShowAddFriend] = useState(false);
  const [query, setQuery] = useState("");
  const [deletingKey, setDeletingKey] = useState("");

  useEffect(() => {
    const u = username || localStorage.username;
    if (!u) return;
    instance
      .post("/getData", { username: u })
      .then((resp) => {
        if (resp && resp.data && resp.data.user) {
          dispatch(userActionCreator(resp.data.user, "AddUser"));
        }
      })
      .catch(() => {});
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const expenses = useMemo(() => {
    const filtered = expensesRaw.filter((e) =>
      String(getDesc(e)).toLowerCase().includes(query.toLowerCase())
    );
    return filtered
      .slice()
      .sort(
        (a, b) => new Date(getDateVal(b) || 0) - new Date(getDateVal(a) || 0)
      );
  }, [expensesRaw, query]);

  const totals = useMemo(() => {
    const total = expensesRaw.reduce((s, e) => s + getAmt(e), 0);
    return { total, owe: "‚Äî", owed: "‚Äî" };
  }, [expensesRaw]);

  async function handleDelete(item) {
    const desc = getDesc(item);
    if (!window.confirm(`Delete expense "${desc}"?`)) return;

    const id = getId(item);
    const who = getWho(item) || null;
    const date = getDateVal(item);
    const amt = getAmt(item);

    // Optimistic UI
    dispatch({ type: "DELETE_EXPENSE", payload: item });
    setDeletingKey(id || sig(item));

    try {
      const result = await tryDeleteOnServer({
        username,
        id,
        who,
        desc,
        amount: amt,
        dateISO: date,
      });

      if (!result.ok) {
        // Rollback if every attempt failed
        dispatch({ type: "APPEND_EXPENSES", payload: item });
        alert(
          "Could not delete on server. Open the console to see which routes/payloads were tried."
        );
        return;
      }

      // Re-fetch and merge (reducer dedupes)
      const refreshed = await instance.post("/getData", { username });
      if (refreshed && refreshed.data && refreshed.data.user) {
        dispatch(userActionCreator(refreshed.data.user, "AddUser"));
      }
    } catch (err) {
      console.error(err);
      dispatch({ type: "APPEND_EXPENSES", payload: item }); // rollback
      alert("Delete failed. Please try again.");
    } finally {
      setDeletingKey("");
    }
  }

  return (
    <div className="dash">
      {/* Header */}
      <header className="dash-header">
        <div className="brand brand--hello">
          <div className="brand-hello">Hi, {username || "User"}</div>
        </div>
        <div className="header-actions">
          <button
            className="btn-outline"
            onClick={() => setShowAddFriend(true)}
          >
            <i className="fas fa-user-plus" /> Add friend
          </button>
          <button
            className="btn-primary"
            onClick={() => setShowAddExpense(true)}
          >
            <i className="fas fa-plus" /> Add expense
          </button>
        </div>
      </header>

      {/* Stats */}
      <section className="stat-grid">
        <div className="stat-card">
          <div className="stat-top">
            <span className="stat-label">Total Spent</span>
            <i className="far fa-chart-bar"></i>
          </div>
          <div className="stat-value">‚Çπ{totals.total.toFixed(2)}</div>
          <div className="stat-sub">Across all expenses</div>
        </div>
        <div className="stat-card">
          <div className="stat-top">
            <span className="stat-label">You Owe</span>
            <i className="far fa-arrow-alt-circle-up"></i>
          </div>
          <div className="stat-value">{String(totals.owe)}</div>
          <div className="stat-sub">Based on splits</div>
        </div>
        <div className="stat-card">
          <div className="stat-top">
            <span className="stat-label">You‚Äôre Owed</span>
            <i className="far fa-arrow-alt-circle-down"></i>
          </div>
          <div className="stat-value">{String(totals.owed)}</div>
          <div className="stat-sub">Friends owe you</div>
        </div>
      </section>

      {/* Main */}
      <section className="content-grid">
        {/* Expenses */}
        <div className="panel">
          <div className="panel-head">
            <div className="panel-title">
              <i className="fas fa-receipt"></i> Recent Expenses
            </div>
            <div className="panel-actions">
              <div className="search">
                <i className="fas fa-search"></i>
                <input
                  className="search-input"
                  placeholder="Search description"
                  value={query}
                  onChange={(e) => setQuery(e.target.value)}
                />
              </div>
            </div>
          </div>

          {expenses.length === 0 ? (
            <div className="empty">
              <div className="empty-emoji">üßæ</div>
              <div className="empty-title">No expenses yet</div>
              <div className="empty-sub">
                Use ‚ÄúAdd expense‚Äù at the top-right.
              </div>
            </div>
          ) : (
            <div className="exp-list">
              {expenses.map((e, i) => {
                const key =
                  (e && (e._id || e.id || e.createdAt || e.date)) || i;
                const isDeleting =
                  deletingKey &&
                  (deletingKey === (e && (e._id || e.id)) ||
                    deletingKey === sig(e));
                return (
                  <ExpenseRow
                    key={key}
                    item={e}
                    deleting={!!isDeleting}
                    onDelete={handleDelete}
                  />
                );
              })}
            </div>
          )}
        </div>

        {/* Friends */}
        <aside className="panel panel-side">
          <div className="panel-head">
            <div className="panel-title">
              <i className="fas fa-user-friends"></i> Friends
            </div>
            <button
              className="btn-mini"
              onClick={() => setShowAddFriend(true)}
              title="Add friend"
            >
              <i className="fas fa-user-plus"></i>
            </button>
          </div>

          {friends.length === 0 ? (
            <div className="empty small">
              <div className="empty-emoji">üë•</div>
              <div className="empty-title">No friends added</div>
              <div className="empty-sub">Click ‚ÄúAdd friend‚Äù.</div>
            </div>
          ) : (
            <ul className="friend-ul">
              {friends.slice(0, 8).map((f, i) => {
                const name =
                  typeof f === "string"
                    ? f
                    : (f && (f.username || f.name || f.email)) || "Friend";
                const key =
                  (f && (f._id || f.id || f.username || f.email)) || i;
                return (
                  <li className="friend-li" key={key}>
                    <div className="avatar">{name[0].toUpperCase()}</div>
                    <div className="friend-name">{name}</div>
                  </li>
                );
              })}
            </ul>
          )}
        </aside>
      </section>

      {/* Popups */}
      {showAddExpense && <AddExpense friend={() => setShowAddExpense(false)} />}
      {showAddFriend && <Friend friend={() => setShowAddFriend(false)} />}
    </div>
  );
}
