import React, { useState } from "react";
import "../styles/signup.css";
import Header from "./Header";
import instance from "../utils/AxiosConfig";
import { useDispatch } from "react-redux";
import { loginSuccess } from "../redux/actionCreator/userAction";

export default function Login({ history }) {
  const [form, setForm] = useState({ email: "", password: "" });
  const [showPwd, setShowPwd] = useState(false);
  const [err, setErr] = useState("");
  const dispatch = useDispatch();

  const onChange = (e) => setForm({ ...form, [e.target.id]: e.target.value });

  const onLogin = async () => {
    setErr("");
    try {
      const { data } = await instance.post("/user/login", form);
      localStorage.setItem("token", data.token);
      localStorage.setItem("user", JSON.stringify(data.user));
      dispatch(loginSuccess(data));
      history.push("/dashboard");
    } catch {
      setErr("Invalid email or password");
    }
  };

  return (
    <div>
      <Header />
      <div className="container signup">
        <div className="signup-logo">
          <img
            src={require("../images/logo.png")}
            alt=""
            style={{ width: 160, objectFit: "contain" }}
          />
        </div>
        <div className="signup-form">
          <h3>WELCOME TO PERSONAL BUDGET TRACKER</h3>
          <label>Email address</label>
          <input
            id="email"
            onChange={onChange}
            className="form-control"
            type="email"
          />
          <label>Password</label>
          <div className="password-field">
            <input
              id="password"
              onChange={onChange}
              className="form-control"
              type={showPwd ? "text" : "password"}
            />
            <button className="show-btn" onClick={() => setShowPwd((s) => !s)}>
              {showPwd ? "Hide" : "Show"}
            </button>
          </div>
          {err && <p style={{ color: "red", marginTop: 8 }}>{err}</p>}
          <div className="btn-row">
            <button className="btn" onClick={onLogin}>
              Log In
            </button>
            <a className="btn btn-outline" href="/signup">
              Sign Up
            </a>
          </div>
        </div>
      </div>
    </div>
  );
}
