import React from "react";
import "../../../styles/frndPop.css";
import { connect } from "react-redux";
import Chips from "react-chips";
import { instance } from "../../../utils/AxiosConfig";
import { store } from "../../../redux/store";
import { userActionCreator } from "../../../redux/actionCreator/userAction";

class AddExpense extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      chips: [],
      description: "",
      amount: "",
      date: this.getToday(),
      loading: false,
      error: "",
      success: "",
    };
  }

  getToday() {
    const d = new Date();
    return (
      d.getFullYear() +
      "-" +
      ("0" + (d.getMonth() + 1)).slice(-2) +
      "-" +
      ("0" + d.getDate()).slice(-2)
    );
  }

  handleChipsChange = (chips) =>
    this.setState({ chips, error: "", success: "" });
  handleInputChange = (e) =>
    this.setState({ [e.target.id]: e.target.value, error: "", success: "" });

  getUsername() {
    const u = this.props.user;
    return (
      (u && u.username) ||
      (u && u.user && u.user.username) ||
      localStorage.username
    );
  }

  save = async () => {
    const { chips, description, amount, date } = this.state;
    const username = this.getUsername();

    const total = parseFloat(amount);
    if (!description || !description.trim())
      return this.setState({ error: "Please enter a description." });
    if (!total || total <= 0)
      return this.setState({ error: "Amount must be a positive number." });
    if (!date) return this.setState({ error: "Please select a date." });

    let payloads;
    if (chips.length > 0) {
      const perPerson = Number((total / (chips.length + 1)).toFixed(2));
      payloads = chips.map((friendName) => ({
        username,
        user: friendName,
        inp: { description, amount: perPerson, date },
      }));
    } else {
      payloads = [
        {
          username,
          user: null,
          inp: { description, amount: Number(total.toFixed(2)), date },
        },
      ];
    }

    try {
      this.setState({ loading: true, error: "", success: "" });

      // Save all; collect any returned docs
      const responses = await Promise.all(
        payloads.map((p) => instance.post("/addExp", p))
      );

      // 1) If server returns a full user in any response, merge it immediately
      let mergedFromServer = responses.find((r) => r && r.data && r.data.user);
      if (mergedFromServer) {
        store.dispatch(
          userActionCreator(mergedFromServer.data.user, "AddUser")
        );
      }

      // 2) If server returns single expense docs, append them immediately (so UI updates now)
      const newItems = [];
      responses.forEach((r) => {
        const d = r && r.data;
        // common shapes:
        //  a) { doc: { user:'X', inp:{description,amount,date}, _id:'...' } }
        //  b) { expense: {...} }
        const exp =
          d && d.doc && (d.doc.inp || d.doc.description || d.doc.amount)
            ? d.doc
            : d && d.expense
            ? d.expense
            : null;
        if (exp) newItems.push(exp);
      });
      if (newItems.length) {
        store.dispatch({ type: "APPEND_EXPENSES", payload: newItems });
      }

      // 3) Do a clean refresh from /getData to stay authoritative
      const refreshed = await instance.post("/getData", { username });
      if (refreshed && refreshed.data && refreshed.data.user) {
        store.dispatch(userActionCreator(refreshed.data.user, "AddUser"));
      }

      this.setState({
        loading: false,
        success: "Expense saved successfully!",
        description: "",
        amount: "",
        chips: [],
      });

      setTimeout(() => {
        this.props.friend();
      }, 400);
    } catch (err) {
      console.error(err);
      this.setState({
        loading: false,
        error: "Failed to save expense. Please try again.",
      });
    }
  };

  render() {
    const { chips, description, amount, date, loading, error, success } =
      this.state;

    const friendSuggestions =
      this.props.user && Array.isArray(this.props.user.friends)
        ? this.props.user.friends
        : this.props.user &&
          this.props.user.user &&
          Array.isArray(this.props.user.user.friends)
        ? this.props.user.user.friends
        : [];

    return (
      <div className="friendPopup">
        <div className="frnd-content">
          <div className="frnd-header">
            <div className="title-wrap">
              <span className="title">Add an expense</span>
              <span className="subtitle">
                Split smartly or keep it personal
              </span>
            </div>
            <button
              className="icon-btn"
              onClick={this.props.friend}
              aria-label="Close"
            >
              <i className="fas fa-times" />
            </button>
          </div>

          <div className="frnd-body">
            <div className="field">
              <label className="field-label">With you and</label>
              <div className="chips-wrap">
                <Chips
                  value={chips}
                  onChange={this.handleChipsChange}
                  suggestions={friendSuggestions}
                  placeholder="Type a friend name and press Enter"
                  fromSuggestionsOnly={false}
                />
              </div>
              <div className="hint">Leave empty for a personal expense.</div>
            </div>

            <div className="field">
              <label htmlFor="description" className="field-label">
                Description
              </label>
              <div className="input-group">
                <i className="far fa-sticky-note input-icon" />
                <input
                  id="description"
                  type="text"
                  className="input"
                  placeholder="e.g., Lunch, Uber, Movie tickets"
                  value={description}
                  onChange={this.handleInputChange}
                  maxLength={60}
                />
              </div>
            </div>

            <div className="grid-2">
              <div className="field">
                <label htmlFor="amount" className="field-label">
                  Amount (₹)
                </label>
                <div className="input-group">
                  <i className="fas fa-rupee-sign input-icon" />
                  <input
                    id="amount"
                    type="number"
                    className="input"
                    placeholder="0.00"
                    value={amount}
                    onChange={this.handleInputChange}
                    step="0.01"
                    min="0"
                  />
                </div>
              </div>

              <div className="field">
                <label htmlFor="date" className="field-label">
                  Date
                </label>
                <div className="input-group">
                  <i className="far fa-calendar input-icon" />
                  <input
                    id="date"
                    type="date"
                    className="input"
                    value={date}
                    onChange={this.handleInputChange}
                  />
                </div>
              </div>
            </div>

            {(() => {
              const t = parseFloat(amount);
              if (!t || t <= 0) return null;
              const each = chips.length
                ? (t / (chips.length + 1)).toFixed(2)
                : null;
              return (
                <div className="split-preview">
                  <i className="fas fa-equals" />
                  <span>
                    {chips.length
                      ? `Split between you + ${chips.length} friend${
                          chips.length > 1 ? "s" : ""
                        }. Each pays: ₹${each}`
                      : `Personal expense. You pay: ₹${t.toFixed(2)}`}
                  </span>
                </div>
              );
            })()}

            {error && (
              <div className="alert alert-error">
                <i className="fas fa-exclamation-circle" />
                <span>{error}</span>
              </div>
            )}
            {success && (
              <div className="alert alert-success">
                <i className="fas fa-check-circle" />
                <span>{success}</span>
              </div>
            )}
          </div>

          <div className="frnd-footer">
            <button
              className="btn btn-ghost"
              onClick={this.props.friend}
              disabled={loading}
            >
              Cancel
            </button>
            <button
              className="btn btn-primary"
              onClick={this.save}
              disabled={loading}
            >
              {loading ? (
                <span className="btn-flex">
                  <span className="spinner"></span> Saving...
                </span>
              ) : (
                "Save expense"
              )}
            </button>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({ user: state.user });
export default connect(mapStateToProps)(AddExpense);
