import React, { useEffect, useState } from "react";
import AddExpense from "./popups/addExpense";
import SettleUp from "./popups/settleUp";
import CategoryChart from "./CategoryChart";
import instance from "../../utils/AxiosConfig";
import {
  computeFromExpenses,
  formatCurrency,
  timeAgo,
} from "../../utils/finance";
import "../../styles/dashboard.css";

export default function MiddleDashboard({
  externalShowAdd,
  externalShowSettle,
  onCloseAdd,
  onCloseSettle,
  onOpenAdd,
  onOpenSettle,
}) {
  const [showAdd, setShowAdd] = useState(false);
  const [showSettle, setShowSettle] = useState(false);
  const [loading, setLoading] = useState(true);
  const [me, setMe] = useState(null);
  const [expenses, setExpenses] = useState([]);

  // sync with header quick-actions
  useEffect(() => setShowAdd(!!externalShowAdd), [externalShowAdd]);
  useEffect(() => setShowSettle(!!externalShowSettle), [externalShowSettle]);

  const openAdd = () => {
    setShowAdd(true);
    onOpenAdd && onOpenAdd();
  };
  const openSettle = () => {
    setShowSettle(true);
    onOpenSettle && onOpenSettle();
  };
  const closeAdd = () => {
    setShowAdd(false);
    onCloseAdd && onCloseAdd();
    refresh();
  };
  const closeSettle = () => {
    setShowSettle(false);
    onCloseSettle && onCloseSettle();
    refresh();
  };

  async function refresh() {
    try {
      setLoading(true);
      const { data } = await instance.get("/user/me");
      setMe({ email: data.email, username: data.username });
      setExpenses(data.expensis || []);
    } catch (e) {
      console.error("Load /me failed", e?.response?.status, e?.response?.data);
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    refresh();
  }, []);

  const stats = computeFromExpenses(me?.email, expenses);
  const { totals, recent, chart } = stats;

  return (
    <main className="middle">
      {/* Stat cards */}
      <section className="cards">
        <div className="card glass">
          <div className="card-top">
            <span className="chip green">Total Balance</span>
          </div>
          <div className="big">{formatCurrency(totals.totalBalance)}</div>
          <div className="mini">{loading ? "Loading…" : "Net of owe/owed"}</div>
        </div>

        <div className="card glass">
          <div className="card-top">
            <span className="chip red">You Owe</span>
          </div>
          <div className="big">{formatCurrency(totals.youOwe)}</div>
          <div className="mini">
            {loading ? "Loading…" : "What you need to pay"}
          </div>
        </div>

        <div className="card glass">
          <div className="card-top">
            <span className="chip blue">Owed to You</span>
          </div>
          <div className="big">{formatCurrency(totals.owedToMe)}</div>
          <div className="mini">
            {loading ? "Loading…" : "What others owe you"}
          </div>
        </div>

        <div className="card glass wide">
          <div className="card-top">
            <span className="chip">Monthly Spend</span>
            <span className="mini">
              {new Date().toLocaleString("en-US", {
                month: "long",
                year: "numeric",
              })}
            </span>
          </div>
          <div className="progress">
            <div
              className="bar"
              style={{
                width:
                  (chart.total
                    ? Math.min(100, (chart.total / (chart.total * 1.2)) * 100)
                    : 0) + "%",
              }}
            />
          </div>
          <div className="mini">{formatCurrency(chart.total)} (your share)</div>
        </div>
      </section>

      {/* Quick actions */}
      <section className="quick-actions">
        <button className="btn" onClick={openAdd}>
          + Add Expense
        </button>
        <button className="btn outline" onClick={openSettle}>
          Settle Up
        </button>
      </section>

      {/* Panels row: Recent + Chart */}
      <section className="panels">
        <div className="panel glass">
          <div className="panel-head">
            <h3>Recent Activity</h3>
            <button className="link" onClick={refresh}>
              Refresh
            </button>
          </div>
          {loading ? (
            <div className="mini">Loading…</div>
          ) : recent.length === 0 ? (
            <div className="mini">
              No activity yet. Add an expense to get started.
            </div>
          ) : (
            <ul className="activity">
              {recent.map((r) => (
                <li key={r.id}>
                  <span className="dot blue" />
                  <span>
                    <b>{r.description}</b> — {formatCurrency(r.amount)}
                  </span>
                  <span className="mini" style={{ marginLeft: 8 }}>
                    Paid by {r.paidBy}
                  </span>
                  <span className="time">{timeAgo(r.date)}</span>
                </li>
              ))}
            </ul>
          )}
        </div>

        <CategoryChart data={chart.data} total={chart.total} />
      </section>

      {showAdd && <AddExpense onClose={closeAdd} />}
      {showSettle && <SettleUp onClose={closeSettle} />}
    </main>
  );
}
