// src/redux/reducers/userReducer.js
const initialState = {
  _id: null,
  username: "",
  email: "",
  friends: [], // IMPORTANT default
  expensis: [], // if other components map this, also default
};

export default function userReducer(state = initialState, action) {
  switch (action.type) {
    case "AddUser":
    case "SET_USER": { // normalize incoming payload to keep arrays stable
      const doc = action.payload || {};
      return {
        ...state,
        ...doc,
        friends: Array.isArray(doc.friends) ? doc.friends : [],
        expensis: Array.isArray(doc.expensis) ? doc.expensis : [],
      };
    }
    // ... your other cases
    default:
      return state;
  }
}
