import React from "react";
import "../../styles/frndPop.css";
import "../../styles/dashboard.css";

const friends = [
  { name: "Alex", balance: 450 },
  { name: "Jamie", balance: -320 },
  { name: "Priya", balance: 0 },
];

function avatar(name) {
  return name.slice(0, 1).toUpperCase();
}

export default function FriendList() {
  return (
    <aside className="friend-list glass">
      <div className="friend-head">
        <h3>Friends</h3>
        <span className="mini">Balances</span>
      </div>
      <ul className="friend-items">
        {friends.map((f) => (
          <li key={f.name} className="friend-item">
            <div className="friend-left">
              <div className="avatar small">{avatar(f.name)}</div>
              <div className="friend-name">{f.name}</div>
            </div>
            <div
              className={`friend-balance ${
                f.balance > 0 ? "pos" : f.balance < 0 ? "neg" : ""
              }`}
            >
              {f.balance > 0
                ? `+₹ ${f.balance}`
                : f.balance < 0
                ? `-₹ ${Math.abs(f.balance)}`
                : "₹ 0"}
            </div>
          </li>
        ))}
      </ul>
    </aside>
  );
}
