import React, { useMemo, useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import AddExpense from "./popups/addExpense";
import Friend from "./popups/Friend";
import "../../styles/dashboard.css";

// Server refresh
import { instance } from "../../utils/AxiosConfig";
import { userActionCreator } from "../../redux/actionCreator/userAction";

/* ---------------- Helpers (safe + beginner-friendly) ---------------- */

// Always return an array so .map() never crashes
function asArray(v) {
  return Array.isArray(v) ? v : [];
}

// Safe number conversion
function toNumber(v) {
  const n = parseFloat(v);
  return isNaN(n) ? 0 : n;
}

// Read nested values like "inp.description"
function pick(obj, paths) {
  for (var i = 0; i < paths.length; i++) {
    var parts = paths[i].split(".");
    var o = obj;
    for (var j = 0; j < parts.length; j++) {
      if (!o || typeof o !== "object" || o[parts[j]] === undefined) {
        o = undefined;
        break;
      }
      o = o[parts[j]];
    }
    if (o !== undefined && o !== null && o !== "") return o;
  }
  return undefined;
}

// Display date like "12 Sep 2025"
function formatDate(iso) {
  var d = iso ? new Date(iso) : null;
  if (!d || isNaN(d.getTime())) return "‚Äî";
  return d.toLocaleDateString(undefined, {
    day: "2-digit",
    month: "short",
    year: "numeric",
  });
}

// If the server uses a different key than "expensis", try to find the array
function detectExpenses(userDoc) {
  if (!userDoc || typeof userDoc !== "object") return [];
  // Common names first
  var common = ["expensis", "expenses", "transactions", "items", "logs"];
  for (var i = 0; i < common.length; i++) {
    var arr = userDoc[common[i]];
    if (Array.isArray(arr) && arr.length) return arr;
  }
  // Fallback: scan all arrays & pick one that looks like expenses
  var candidates = [];
  for (var k in userDoc) {
    if (!userDoc.hasOwnProperty(k)) continue;
    var v = userDoc[k];
    if (
      Array.isArray(v) &&
      v.some(function (x) {
        return (
          x &&
          typeof x === "object" &&
          (x.inp ||
            x.description ||
            x.amount !== undefined ||
            x.title ||
            x.note ||
            x.date ||
            (x.user && x.inp))
        );
      })
    ) {
      candidates.push(v);
    }
  }
  candidates.sort(function (a, b) {
    return b.length - a.length;
  });
  return candidates[0] || [];
}

// Field extractors ‚Äì support flat or nested "inp.*"
function getDesc(e) {
  return (
    pick(e, ["description", "title", "note", "inp.description"]) || "Untitled"
  );
}
function getAmt(e) {
  return toNumber(pick(e, ["amount", "inp.amount", "value"]));
}
function getDateVal(e) {
  return pick(e, ["date", "createdAt", "updatedAt", "inp.date"]) || null;
}
function getWho(e) {
  return pick(e, ["user", "friend", "paidBy", "with"]);
}

/* ---------------- Row Component ---------------- */

function ExpenseRow(props) {
  var item = props.item;
  var desc = getDesc(item);
  var amt = getAmt(item);
  var who = getWho(item);
  var dt = getDateVal(item);

  return (
    <div className="exp-row">
      <div className="exp-main">
        <div className="exp-desc">{desc}</div>
        <div className="exp-meta">
          <span className="pill">{formatDate(dt)}</span>
          {who ? (
            <span className="pill pill-ghost">@ {String(who)}</span>
          ) : null}
        </div>
      </div>
      <div className="exp-right">
        <div className="exp-amount">‚Çπ{amt.toFixed(2)}</div>
      </div>
    </div>
  );
}

/* ---------------- Dashboard ---------------- */

export default function Dashboard() {
  var dispatch = useDispatch();

  // Works with both:
  // A) state.user = { ...userDoc }
  // B) state.user = { user: { ...userDoc } }
  var userState = useSelector(function (state) {
    return (state && state.user) || {};
  });
  var userDoc =
    userState && userState._id !== undefined ? userState : userState.user || {};

  var username = (userDoc && userDoc.username) || "User";
  var friends = asArray(userDoc && userDoc.friends);

  // Prefer the canonical "expensis" list set by reducer; fallback to detection
  var primary = Array.isArray(userDoc.expensis) ? userDoc.expensis : [];
  var expensesRaw = primary.length ? primary : detectExpenses(userDoc);

  var _useState = useState(false),
    showAddExpense = _useState[0],
    setShowAddExpense = _useState[1];

  var _useState2 = useState(false),
    showAddFriend = _useState2[0],
    setShowAddFriend = _useState2[1];

  var _useState3 = useState(""),
    query = _useState3[0],
    setQuery = _useState3[1];

  // Safety: refresh once on mount so we always have the latest user data
  useEffect(function () {
    var u = username || localStorage.username;
    if (!u) return;
    instance
      .post("/getData", { username: u })
      .then(function (resp) {
        if (resp && resp.data && resp.data.user) {
          dispatch(userActionCreator(resp.data.user, "AddUser"));
        }
      })
      .catch(function () {});
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  var expenses = useMemo(
    function () {
      var filtered = asArray(expensesRaw).filter(function (e) {
        return String(getDesc(e)).toLowerCase().includes(query.toLowerCase());
      });
      return filtered.slice().sort(function (a, b) {
        return (
          new Date(getDateVal(b) || 0).getTime() -
          new Date(getDateVal(a) || 0).getTime()
        );
      });
    },
    [expensesRaw, query]
  );

  var totals = useMemo(
    function () {
      var total = asArray(expensesRaw).reduce(function (s, e) {
        return s + getAmt(e);
      }, 0);
      return { total: total, owe: "‚Äî", owed: "‚Äî" };
    },
    [expensesRaw]
  );

  return (
    <div className="dash">
      {/* Header: bold greeting only (per your request) + clear actions */}
      <header className="dash-header">
        <div className="brand brand--hello">
          <div className="brand-hello">Hi, {username}</div>
        </div>

        <div className="header-actions">
          <button
            className="btn-outline"
            onClick={function () {
              setShowAddFriend(true);
            }}
          >
            <i className="fas fa-user-plus" /> Add friend
          </button>
          <button
            className="btn-primary"
            onClick={function () {
              setShowAddExpense(true);
            }}
          >
            <i className="fas fa-plus" /> Add expense
          </button>
        </div>
      </header>

      {/* Stat cards */}
      <section className="stat-grid">
        <div className="stat-card">
          <div className="stat-top">
            <span className="stat-label">Total Spent</span>
            <i className="far fa-chart-bar"></i>
          </div>
          <div className="stat-value">‚Çπ{totals.total.toFixed(2)}</div>
          <div className="stat-sub">Across all expenses</div>
        </div>

        <div className="stat-card">
          <div className="stat-top">
            <span className="stat-label">You Owe</span>
            <i className="far fa-arrow-alt-circle-up"></i>
          </div>
          <div className="stat-value">{String(totals.owe)}</div>
          <div className="stat-sub">Based on splits</div>
        </div>

        <div className="stat-card">
          <div className="stat-top">
            <span className="stat-label">You‚Äôre Owed</span>
            <i className="far fa-arrow-alt-circle-down"></i>
          </div>
          <div className="stat-value">{String(totals.owed)}</div>
          <div className="stat-sub">Friends owe you</div>
        </div>
      </section>

      {/* Content */}
      <section className="content-grid">
        {/* Left: Recent Expenses */}
        <div className="panel">
          <div className="panel-head">
            <div className="panel-title">
              <i className="fas fa-receipt"></i> Recent Expenses
            </div>
            <div className="panel-actions">
              <div className="search">
                <i className="fas fa-search"></i>
                <input
                  className="search-input"
                  placeholder="Search description"
                  value={query}
                  onChange={function (e) {
                    setQuery(e.target.value);
                  }}
                />
              </div>
            </div>
          </div>

          {expenses.length === 0 ? (
            <div className="empty">
              <div className="empty-emoji">üßæ</div>
              <div className="empty-title">No expenses yet</div>
              <div className="empty-sub">
                Use ‚ÄúAdd expense‚Äù at the top-right.
              </div>
            </div>
          ) : (
            <div className="exp-list">
              {expenses.map(function (e, i) {
                var key = (e && (e._id || e.id || e.createdAt || e.date)) || i;
                return <ExpenseRow key={key} item={e} />;
              })}
            </div>
          )}
        </div>

        {/* Right: Friends */}
        <aside className="panel panel-side">
          <div className="panel-head">
            <div className="panel-title">
              <i className="fas fa-user-friends"></i> Friends
            </div>
            <button
              className="btn-mini"
              onClick={function () {
                setShowAddFriend(true);
              }}
              title="Add friend"
            >
              <i className="fas fa-user-plus"></i>
            </button>
          </div>

          {friends.length === 0 ? (
            <div className="empty small">
              <div className="empty-emoji">üë•</div>
              <div className="empty-title">No friends added</div>
              <div className="empty-sub">Click ‚ÄúAdd friend‚Äù.</div>
            </div>
          ) : (
            <ul className="friend-ul">
              {friends.slice(0, 8).map(function (f, i) {
                var name =
                  typeof f === "string"
                    ? f
                    : (f && (f.username || f.name || f.email)) || "Friend";
                var key = (f && (f._id || f.id || f.username || f.email)) || i;
                return (
                  <li className="friend-li" key={key}>
                    <div className="avatar">{name[0].toUpperCase()}</div>
                    <div className="friend-name">{name}</div>
                  </li>
                );
              })}
            </ul>
          )}
        </aside>
      </section>

      {/* Popups */}
      {showAddExpense ? (
        <AddExpense
          friend={function () {
            setShowAddExpense(false);
          }}
        />
      ) : null}
      {showAddFriend ? (
        <Friend
          friend={function () {
            setShowAddFriend(false);
          }}
        />
      ) : null}
    </div>
  );
}
