import React, { useMemo, useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import AddExpense from "./popups/addExpense";
import Friend from "./popups/Friend";
import "../../styles/dashboard.css";

// NEW: fetch + dispatch (for a clean refresh)
import { instance } from "../../utils/AxiosConfig";
import { userActionCreator } from "../../redux/actionCreator/userAction";

/* ---------------- Helpers (robust against different API shapes) ---------------- */
const asArray = (v) => (Array.isArray(v) ? v : []);
const toNumber = (v) => {
  const n = parseFloat(v);
  return isNaN(n) ? 0 : n;
};

// read a.b.c safely
function pick(obj, paths) {
  for (let p of paths) {
    const val = p
      .split(".")
      .reduce((o, k) => (o && o[k] !== undefined ? o[k] : undefined), obj);
    if (val !== undefined && val !== null && val !== "") return val;
  }
  return undefined;
}

function formatDate(iso) {
  const d = iso ? new Date(iso) : null;
  if (!d || isNaN(d.getTime())) return "‚Äî";
  return d.toLocaleDateString(undefined, {
    day: "2-digit",
    month: "short",
    year: "numeric",
  });
}

// Try to find the right expenses array in whatever the server sent
function detectExpenses(userDoc) {
  if (!userDoc || typeof userDoc !== "object") return [];
  // Common names first
  for (const key of ["expensis", "expenses", "transactions", "items", "logs"]) {
    const arr = userDoc[key];
    if (Array.isArray(arr) && arr.length) return arr;
  }
  // Fallback: scan all arrays & pick the one that looks like expenses
  const candidates = [];
  for (const k of Object.keys(userDoc)) {
    const v = userDoc[k];
    if (
      Array.isArray(v) &&
      v.some(
        (x) =>
          x &&
          typeof x === "object" &&
          (x.inp ||
            x.description ||
            x.amount !== undefined ||
            x.title ||
            x.note ||
            x.date ||
            (x.user && x.inp))
      )
    ) {
      candidates.push(v);
    }
  }
  candidates.sort((a, b) => b.length - a.length);
  return candidates[0] || [];
}

// Extractors ‚Äì handle flat or nested (inp.*)
const getDesc = (e) =>
  pick(e, ["description", "title", "note", "inp.description"]) || "Untitled";
const getAmt = (e) => toNumber(pick(e, ["amount", "inp.amount", "value"]));
const getDate = (e) =>
  pick(e, ["date", "createdAt", "updatedAt", "inp.date"]) || null;
const getWho = (e) => pick(e, ["user", "friend", "paidBy", "with"]);

function ExpenseRow({ item }) {
  const desc = getDesc(item);
  const amt = getAmt(item);
  const who = getWho(item);
  const dt = getDate(item);

  return (
    <div className="exp-row">
      <div className="exp-main">
        <div className="exp-desc">{desc}</div>
        <div className="exp-meta">
          <span className="pill">{formatDate(dt)}</span>
          {who ? (
            <span className="pill pill-ghost">@ {String(who)}</span>
          ) : null}
        </div>
      </div>
      <div className="exp-right">
        <div className="exp-amount">‚Çπ{amt.toFixed(2)}</div>
      </div>
    </div>
  );
}

export default function Dashboard() {
  const dispatch = useDispatch();

  // Works with both shapes:
  //  A) state.user = { ...userDoc }
  //  B) state.user = { user: { ...userDoc } }
  const userState = useSelector((state) => (state && state.user) || {});
  const userDoc =
    userState && userState._id !== undefined ? userState : userState.user || {};

  const username = userDoc && (userDoc.username || "User");
  const friends = asArray(userDoc && userDoc.friends);

  // KEY: detect the expenses array dynamically
  const raw = detectExpenses(userDoc);

  const [showAddExpense, setShowAddExpense] = useState(false);
  const [showAddFriend, setShowAddFriend] = useState(false);
  const [query, setQuery] = useState("");

  // Refresh from server on first mount (extra safety)
  useEffect(() => {
    const u = username || localStorage.username;
    if (!u) return;
    instance
      .post("/getData", { username: u })
      .then((resp) => {
        if (resp && resp.data && resp.data.user) {
          dispatch(userActionCreator(resp.data.user, "AddUser"));
        }
      })
      .catch(() => {});
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []); // run once

  const expenses = useMemo(() => {
    const filtered = asArray(raw).filter((e) =>
      String(getDesc(e)).toLowerCase().includes(query.toLowerCase())
    );
    return filtered
      .slice()
      .sort((a, b) => new Date(getDate(b) || 0) - new Date(getDate(a) || 0));
  }, [raw, query]);

  const totals = useMemo(() => {
    const total = asArray(raw).reduce((s, e) => s + getAmt(e), 0);
    return { total, owe: "‚Äî", owed: "‚Äî" };
  }, [raw]);

  return (
    <div className="dash">
      {/* Greeting + actions */}
      <header className="dash-header">
        <div className="brand brand--hello">
          <div className="brand-hello">Hi, {username}</div>
        </div>
        <div className="header-actions">
          <button
            className="btn-outline"
            onClick={() => setShowAddFriend(true)}
          >
            <i className="fas fa-user-plus" /> Add friend
          </button>
          <button
            className="btn-primary"
            onClick={() => setShowAddExpense(true)}
          >
            <i className="fas fa-plus" /> Add expense
          </button>
        </div>
      </header>

      {/* Stat cards */}
      <section className="stat-grid">
        <div className="stat-card">
          <div className="stat-top">
            <span className="stat-label">Total Spent</span>
            <i className="far fa-chart-bar"></i>
          </div>
          <div className="stat-value">‚Çπ{totals.total.toFixed(2)}</div>
          <div className="stat-sub">Across all expenses</div>
        </div>
        <div className="stat-card">
          <div className="stat-top">
            <span className="stat-label">You Owe</span>
            <i className="far fa-arrow-alt-circle-up"></i>
          </div>
          <div className="stat-value">{String(totals.owe)}</div>
          <div className="stat-sub">Based on splits</div>
        </div>
        <div className="stat-card">
          <div className="stat-top">
            <span className="stat-label">You‚Äôre Owed</span>
            <i className="far fa-arrow-alt-circle-down"></i>
          </div>
          <div className="stat-value">{String(totals.owed)}</div>
          <div className="stat-sub">Friends owe you</div>
        </div>
      </section>

      {/* Main content */}
      <section className="content-grid">
        {/* Left: Expenses */}
        <div className="panel">
          <div className="panel-head">
            <div className="panel-title">
              <i className="fas fa-receipt"></i> Recent Expenses
            </div>
            <div className="panel-actions">
              <div className="search">
                <i className="fas fa-search"></i>
                <input
                  className="search-input"
                  placeholder="Search description"
                  value={query}
                  onChange={(e) => setQuery(e.target.value)}
                />
              </div>
            </div>
          </div>

          {expenses.length === 0 ? (
            <div className="empty">
              <div className="empty-emoji">üßæ</div>
              <div className="empty-title">No expenses yet</div>
              <div className="empty-sub">
                Use ‚ÄúAdd expense‚Äù at the top-right.
              </div>
            </div>
          ) : (
            <div className="exp-list">
              {expenses.map((e, i) => {
                const key =
                  (e && (e._id || e.id || e.createdAt || e.date)) || i;
                return <ExpenseRow key={key} item={e} />;
              })}
            </div>
          )}
        </div>

        {/* Right: Friends */}
        <aside className="panel panel-side">
          <div className="panel-head">
            <div className="panel-title">
              <i className="fas fa-user-friends"></i> Friends
            </div>
            <button
              className="btn-mini"
              onClick={() => setShowAddFriend(true)}
              title="Add friend"
            >
              <i className="fas fa-user-plus"></i>
            </button>
          </div>

          {friends.length === 0 ? (
            <div className="empty small">
              <div className="empty-emoji">üë•</div>
              <div className="empty-title">No friends added</div>
              <div className="empty-sub">Click ‚ÄúAdd friend‚Äù.</div>
            </div>
          ) : (
            <ul className="friend-ul">
              {friends.slice(0, 8).map((f, i) => {
                const name =
                  typeof f === "string"
                    ? f
                    : (f && (f.username || f.name || f.email)) || "Friend";
                const key =
                  (f && (f._id || f.id || f.username || f.email)) || i;
                return (
                  <li className="friend-li" key={key}>
                    <div className="avatar">{name[0].toUpperCase()}</div>
                    <div className="friend-name">{name}</div>
                  </li>
                );
              })}
            </ul>
          )}
        </aside>
      </section>

      {/* Popups */}
      {showAddExpense && <AddExpense friend={() => setShowAddExpense(false)} />}
      {showAddFriend && <Friend friend={() => setShowAddFriend(false)} />}
    </div>
  );
}
