import React, { useState } from "react";
import { instance } from "../utils/AxiosConfig";
import handleApiError from "../utils/handleApiError";

export default function Signup() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [msg, setMsg] = useState("");

  const onSignup = async () => {
    setMsg("");
    try {
      const res = await instance.post("/signup", { username, email, password });
      setMsg(`Created user: ${res.data.user.username}`);
    } catch (err) {
      handleApiError(err, setMsg);
    }
  };

  return (
    <div
      style={{ maxWidth: 420, margin: "20px auto", fontFamily: "system-ui" }}
    >
      <h2>Sign up</h2>
      {msg && (
        <div
          style={{
            padding: 10,
            background: "#d1fae5",
            borderRadius: 8,
            marginBottom: 12,
          }}
        >
          {msg}
        </div>
      )}

      <label>Username</label>
      <input
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        style={{
          width: "100%",
          padding: 10,
          margin: "6px 0 12px",
          borderRadius: 8,
          border: "1px solid #ccc",
        }}
      />

      <label>Email</label>
      <input
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        style={{
          width: "100%",
          padding: 10,
          margin: "6px 0 12px",
          borderRadius: 8,
          border: "1px solid #ccc",
        }}
      />

      <label>Password</label>
      <input
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        style={{
          width: "100%",
          padding: 10,
          margin: "6px 0 12px",
          borderRadius: 8,
          border: "1px solid #ccc",
        }}
      />

      <button
        onClick={onSignup}
        style={{
          padding: "10px 16px",
          borderRadius: 8,
          background: "#16a34a",
          color: "#fff",
          border: 0,
        }}
      >
        Create account
      </button>
    </div>
  );
}
