import React, { useState } from "react";
import AddExpense from "./popups/addExpense";
import SettleUp from "./popups/settleUp";
import "../../styles/Dashboard.css";

export default function MiddleDashboard() {
  const [showAdd, setShowAdd] = useState(false);
  const [showSettle, setShowSettle] = useState(false);

  return (
    <main className="middle">
      <div className="cards">
        <div className="card">
          <h3>Total Balance</h3>
          <div className="big">₹ 0</div>
        </div>
        <div className="card">
          <h3>You Owe</h3>
          <div className="big">₹ 0</div>
        </div>
        <div className="card">
          <h3>Owed To You</h3>
          <div className="big">₹ 0</div>
        </div>
      </div>

      <div className="actions-row">
        <button onClick={() => setShowAdd(true)}>+ Add Expense</button>
        <button className="btn-outline" onClick={() => setShowSettle(true)}>
          Settle Up
        </button>
      </div>

      {showAdd && <AddExpense onClose={() => setShowAdd(false)} />}
      {showSettle && <SettleUp onClose={() => setShowSettle(false)} />}
    </main>
  );
}
