// src/components/FriendList.jsx
import React from "react";
import { connect } from "react-redux";

// Small helper: always return an array
function asArray(v) {
  return Array.isArray(v) ? v : [];
}

const FriendList = ({ friends }) => {
  const list = asArray(friends);

  if (list.length === 0) {
    return (
      <div className="empty-state" style={{ padding: 16, color: "#64748b" }}>
        No friends yet.
      </div>
    );
  }

  return (
    <ul
      className="friend-list"
      style={{ listStyle: "none", padding: 0, margin: 0 }}
    >
      {list.map((f, idx) => {
        // each friend can be a string or an object
        const key = (f && (f._id || f.id || f.username || f.email)) || idx;
        const label =
          typeof f === "string"
            ? f
            : (f && (f.username || f.name || f.email)) || "Friend";
        return (
          <li
            key={key}
            style={{
              padding: "10px 12px",
              borderBottom: "1px solid rgba(148,163,184,.25)",
            }}
          >
            {label}
          </li>
        );
      })}
    </ul>
  );
};

const mapStateToProps = (state) => ({
  // Guard state.user and state.user.friends
  friends: Array.isArray(state && state.user && state.user.friends)
    ? state.user.friends
    : [],
});

export default connect(mapStateToProps)(FriendList);
