import React, { useEffect, useMemo, useState } from "react";
import { useSelector } from "react-redux";
import { addExpense } from "../../../data/meApi";
import "../../../styles/frndPop.css";

export default function AddExpense({ onClose }) {
  const { me, friends, categories } = useSelector((s) => s.me);

  // --- helpers: show names in the UI, keep emails as values ---
  const nameFromEmail = (email) => {
    if (!email) return "";
    const local = String(email).split("@")[0];
    return local
      .split(/[.\-_]+/)
      .filter(Boolean)
      .map((w) => w[0]?.toUpperCase() + w.slice(1))
      .join(" ");
  };

  const people = useMemo(() => {
    const mine = me?.email
      ? [{ email: me.email, label: me?.username || nameFromEmail(me.email) }]
      : [];
    const frs = (friends || []).map((e) => ({
      email: e,
      label: nameFromEmail(e) || e,
    }));
    // unique by email
    const map = new Map();
    [...mine, ...frs].forEach((p) => map.set(p.email, p));
    return Array.from(map.values());
  }, [me, friends]);

  // --- form state ---
  const [form, setForm] = useState({
    description: "",
    amount: "",
    paidBy: "",
    splitWith: [], // array of emails (selected)
    category: "Other",
    date: new Date().toISOString().slice(0, 10),
  });
  const [msg, setMsg] = useState("");
  const [loading, setLoading] = useState(false);

  // defaults once data loads
  useEffect(() => {
    if (me?.email && !form.paidBy) {
      setForm((f) => ({ ...f, paidBy: me.email }));
    }
    if ((categories || []).length && form.category === "Other") {
      // keep 'Other' if present, else first category
      const hasOther = categories.some((c) => c.name === "Other");
      if (!hasOther) setForm((f) => ({ ...f, category: categories[0].name }));
    }
  }, [me, categories]); // eslint-disable-line react-hooks/exhaustive-deps

  const onChange = (e) => setForm({ ...form, [e.target.id]: e.target.value });

  const onChangeSplit = (e) => {
    const selected = Array.from(e.target.selectedOptions).map((o) => o.value);
    setForm({ ...form, splitWith: selected });
  };

  const submit = async () => {
    setMsg("");
    if (!form.description || !form.amount || !form.paidBy) {
      setMsg("Please fill Description, Amount and Paid by");
      return;
    }
    setLoading(true);
    try {
      // Participants = paidBy + splitWith (unique)
      const participants = Array.from(
        new Set([form.paidBy, ...form.splitWith])
      );
      // Legacy `paidTo` for server: everyone except payer
      const paidToList = participants
        .filter((e) => e.toLowerCase() !== form.paidBy.toLowerCase())
        .join(", ");

      await addExpense({
        description: form.description,
        amount: form.amount,
        paidBy: form.paidBy,
        paidTo: paidToList, // legacy field (server still accepts)
        splitWith: participants.join(", "), // canonical list for compute helpers
        category: form.category,
        date: form.date,
      });

      onClose(); // will refresh dashboard
    } catch (e) {
      setMsg("Error adding expense");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal">
      <div className="modal-card glass">
        <h3>Add Expense</h3>

        <div className="grid2 grid-gap-lg">
          {/* Row 1 */}
          <input
            id="description"
            placeholder="Description (e.g., Dinner)"
            value={form.description}
            onChange={onChange}
          />
          <input
            id="amount"
            placeholder="Amount"
            type="number"
            value={form.amount}
            onChange={onChange}
          />

          {/* Row 2: choose by NAME */}
          <select
            id="paidBy"
            value={form.paidBy}
            onChange={onChange}
            className="select"
            title="Paid by"
          >
            <option value="" disabled>
              Paid by (choose a person)
            </option>
            {people.map((p) => (
              <option key={p.email} value={p.email}>
                {p.label} ({p.email})
              </option>
            ))}
          </select>

          {/* Multi-select for splitWith */}
          <select
            multiple
            id="splitWith"
            value={form.splitWith}
            onChange={onChangeSplit}
            className="select multi"
            title="Split with"
          >
            {people.map((p) => (
              <option key={p.email} value={p.email}>
                {p.label} ({p.email})
              </option>
            ))}
          </select>

          {/* Row 3 */}
          <select
            id="category"
            value={form.category}
            onChange={onChange}
            className="select"
          >
            {(categories?.length ? categories : [{ name: "Other" }]).map(
              (c) => (
                <option key={c.name} value={c.name}>
                  {c.name}
                </option>
              )
            )}
          </select>

          <input id="date" type="date" value={form.date} onChange={onChange} />
        </div>

        <div className="modal-actions">
          <button className="btn" onClick={submit} disabled={loading}>
            {loading ? "Adding…" : "Add"}
          </button>
          <button className="btn outline" onClick={onClose}>
            Close
          </button>
        </div>

        {msg && (
          <p className="mini" style={{ marginTop: 8 }}>
            {msg}
          </p>
        )}
      </div>
    </div>
  );
}
