import React, { useState } from "react";
import instance from "../../../utils/AxiosConfig";
import "../../../styles/frndPop.css";

export default function AddExpense({ onClose }) {
  const [form, setForm] = useState({
    description: "",
    amount: "",
    paidBy: "",
    paidTo: "",
  });
  const [msg, setMsg] = useState("");
  const [loading, setLoading] = useState(false);

  const onChange = (e) => setForm({ ...form, [e.target.id]: e.target.value });

  const add = async () => {
    setMsg("");
    setLoading(true);
    try {
      await instance.post("/user/expense/add", form);
      setMsg("✅ Expense added");
      setForm({ description: "", amount: "", paidBy: "", paidTo: "" });
    } catch (e) {
      setMsg(e?.response?.data?.msg || "❌ Error adding expense");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal">
      <div className="modal-card glass">
        <h3>Add Expense</h3>
        <div className="grid2">
          <input
            id="description"
            placeholder="Description (e.g., Dinner)"
            value={form.description}
            onChange={onChange}
          />
          <input
            id="amount"
            placeholder="Amount"
            type="number"
            value={form.amount}
            onChange={onChange}
          />
          <input
            id="paidBy"
            placeholder="Paid by (email)"
            value={form.paidBy}
            onChange={onChange}
          />
          <input
            id="paidTo"
            placeholder="Paid to (email)"
            value={form.paidTo}
            onChange={onChange}
          />
        </div>
        <div className="modal-actions">
          <button className="btn" onClick={add} disabled={loading}>
            {loading ? "Adding…" : "Add"}
          </button>
          <button className="btn outline" onClick={onClose}>
            Close
          </button>
        </div>
        {msg && (
          <p className="mini" style={{ marginTop: 8 }}>
            {msg}
          </p>
        )}
      </div>
    </div>
  );
}
