import React, { useEffect } from "react";
import { useSelector } from "react-redux";
import "../../styles/frndPop.css";
import "../../styles/dashboard.css";
import { computeFromExpenses, formatCurrency } from "../../utils/finance";
import { loadMe, settleWithFriend } from "../../data/meApi";

function Avatar({ nameOrEmail }) {
  const ch = (nameOrEmail || "?").trim().charAt(0).toUpperCase();
  return <div className="avatar small">{ch}</div>;
}

export default function FriendList() {
  const { me, expenses } = useSelector((s) => s.me);

  // ✅ load data directly (do NOT dispatch(loadMe()))
  useEffect(() => {
    loadMe();
  }, []);

  const { perFriend } = computeFromExpenses(me?.email, expenses);

  return (
    <aside className="friend-list glass">
      <div className="friend-head">
        <h3>Friends</h3>
        <span className="mini">Balances</span>
      </div>
      {perFriend.length === 0 ? (
        <div className="mini">No balances yet.</div>
      ) : (
        <ul className="friend-items">
          {perFriend.map((f) => (
            <li key={f.friend} className="friend-item">
              <div className="friend-left">
                <Avatar nameOrEmail={f.friend} />
                <div className="friend-name">{f.friend}</div>
              </div>
              <div style={{ display: "flex", alignItems: "center", gap: 6 }}>
                <div
                  className={`friend-balance ${
                    f.value > 0 ? "pos" : f.value < 0 ? "neg" : ""
                  }`}
                >
                  {f.value > 0
                    ? `+${formatCurrency(f.value)}`
                    : f.value < 0
                    ? `-${formatCurrency(Math.abs(f.value))}`
                    : formatCurrency(0)}
                </div>
                {f.value !== 0 && (
                  <button
                    className="btn ghost"
                    onClick={() =>
                      settleWithFriend(f.friend, Math.abs(f.value))
                    }
                  >
                    Settle
                  </button>
                )}
              </div>
            </li>
          ))}
        </ul>
      )}
    </aside>
  );
}
