import React, { useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom";
import "../styles/siteHeader.css";
import { toggleTheme, getInitialTheme } from "../utils/theme";

export default function Header() {
  const { pathname } = useLocation();
  const [theme, setTheme] = useState(getInitialTheme());

  useEffect(() => {
    // sync with external changes (e.g., OS switch)
    const m = window.matchMedia("(prefers-color-scheme: light)");
    const onChange = () =>
      setTheme(
        document.documentElement.getAttribute("data-theme") || getInitialTheme()
      );
    m.addEventListener?.("change", onChange);
    return () => m.removeEventListener?.("change", onChange);
  }, []);

  const onToggle = () => setTheme(toggleTheme());

  return (
    <header className="site-header glass">
      <div className="site-header__left">
        <Link to="/" className="brand">
          <span className="brand-mark">üí∞</span>
          <span className="brand-text">Budget Tracker</span>
        </Link>
      </div>

      <nav className="site-header__nav">
        <button
          className="theme-toggle"
          onClick={onToggle}
          aria-label="Toggle theme"
        >
          {theme === "dark" ? "‚òÄÔ∏è" : "üåô"}
        </button>
        <Link className={`nav-link ${pathname === "/" ? "active" : ""}`} to="/">
          Home
        </Link>
        <Link
          className={`nav-link ${pathname === "/login" ? "active" : ""}`}
          to="/login"
        >
          Login
        </Link>
        <Link className="btn" to="/signup">
          Sign Up
        </Link>
      </nav>
    </header>
  );
}
