import React from "react";
import "../../../styles/frndPop.css";
import { connect } from "react-redux";
import Chips from "react-chips";
import { instance } from "../../../utils/AxiosConfig";
import { store } from "../../../redux/store";
import { userActionCreator } from "../../../redux/actionCreator/userAction";

class AddExpense extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      chips: [], // selected friends (array of strings)
      description: "",
      amount: "",
      date: this.getToday(), // default to today
      loading: false,
      error: "",
    };
  }

  getToday() {
    const d = new Date();
    return (
      d.getFullYear() +
      "-" +
      ("0" + (d.getMonth() + 1)).slice(-2) +
      "-" +
      ("0" + d.getDate()).slice(-2)
    );
  }

  handleChipsChange = (chips) => {
    this.setState({ chips }); // no need to spread previous state
  };

  handleInputChange = (e) => {
    const { id, value } = e.target;
    this.setState({ [id]: value, error: "" });
  };

  // Helper to preview the split per person
  getPerPersonShare() {
    const { amount, chips } = this.state;
    const total = parseFloat(amount);
    if (!total || total <= 0) return null;
    const people = chips.length + 1; // you + friends
    const share = total / people;
    // keep 2 decimals for currency; adjust if your backend wants integers
    return share.toFixed(2);
  }

  save = async () => {
    const { chips, description, amount, date } = this.state;
    const { user } = this.props;

    // Basic validation
    const total = parseFloat(amount);
    if (!description.trim()) {
      this.setState({ error: "Please enter a description." });
      return;
    }
    if (!total || total <= 0) {
      this.setState({ error: "Amount must be a positive number." });
      return;
    }
    if (!date) {
      this.setState({ error: "Please select a date." });
      return;
    }
    if (chips.length === 0) {
      // If you want to allow personal (non-split) expenses, remove this block
      // and see the NOTE below.
      this.setState({ error: "Add at least one friend to split with." });
      return;
    }

    const perPerson = Number((total / (chips.length + 1)).toFixed(2));

    const payloads = chips.map((friendName) => ({
      username: user.username, // the current user
      user: friendName, // friend being charged
      inp: {
        description,
        amount: perPerson,
        date,
      },
    }));

    try {
      this.setState({ loading: true, error: "" });

      const responses = await Promise.all(
        payloads.map((p) => instance.post("/addExp", p))
      );

      // Dispatch each returned doc to Redux (keeps your list in sync)
      responses.forEach((resp) => {
        const action = userActionCreator(resp.data.doc, "AddUser");
        store.dispatch(action);
      });

      // Close popup after everything succeeds
      this.props.friend();
    } catch (err) {
      console.error(err);
      this.setState({
        error: "Failed to save expense. Please try again.",
        loading: false,
      });
      return;
    }

    this.setState({ loading: false });
  };

  render() {
    const { chips, description, amount, date, loading, error } = this.state;
    // If your friends are objects, map them to strings:
    // const friendSuggestions = (this.props.user.friends || []).map(f => f.username || f);
    const friendSuggestions = this.props.user.friends || [];

    const perPerson = this.getPerPersonShare();

    return (
      <div className="friendPopup">
        <div className="frnd-content">
          <div className="frnd-header">
            <span>Add an expense</span>
            <button
              className="float-right"
              onClick={this.props.friend}
              aria-label="Close"
            >
              <i className="fas fa-times" />
            </button>
          </div>

          <div className="exp-inp">
            <label>With you and</label>
            <div className="exp-name">
              <Chips
                value={chips}
                onChange={this.handleChipsChange}
                suggestions={friendSuggestions}
                placeholder="Type a friend's name and press Enter"
                fromSuggestionsOnly={false} // set true if you want to restrict to existing friends
              />
            </div>
          </div>

          <div className="exp-inp2">
            <input
              id="description"
              type="text"
              placeholder="Enter Description"
              value={description}
              onChange={this.handleInputChange}
            />

            <input
              id="amount"
              type="number"
              placeholder="Enter Amount (₹)"
              value={amount}
              onChange={this.handleInputChange}
              step="0.01"
              min="0"
            />

            <br />

            <input
              id="date"
              type="date"
              value={date}
              onChange={this.handleInputChange}
            />

            {/* Tiny helper line for the split */}
            {perPerson && chips.length > 0 && (
              <small
                style={{ display: "block", marginTop: 6, color: "#6b7280" }}
              >
                Split between you and {chips.length} friend
                {chips.length > 1 ? "s" : ""}. Each pays: ₹{perPerson}
              </small>
            )}

            {error && (
              <p style={{ color: "tomato", marginTop: 10 }}>
                <i className="fas fa-exclamation-circle" /> {error}
              </p>
            )}
          </div>

          <div className="pop-btn pop-btns">
            <button className="btn Add" onClick={this.save} disabled={loading}>
              {loading ? "Saving..." : "Save"}
            </button>

            <button
              className="btn cut"
              onClick={this.props.friend}
              disabled={loading}
            >
              Close
            </button>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  user: state.user,
});

export default connect(mapStateToProps)(AddExpense);
