import React, { useState } from "react";
import { useSelector } from "react-redux";
import { editExpense } from "../../../data/meApi";
import "../../../styles/frndPop.css";

export default function EditExpense({ item, onClose }) {
  const cats = useSelector((s) => s.me.categories);
  const [form, setForm] = useState({
    description: item.description || "",
    amount: item.amount || "",
    paidBy: item.paidBy || "",
    splitWith: item.splitWith || `${item.paidBy}, ${item.paidTo || ""}`,
    category: item.category || "Other",
    date: item.date
      ? item.date.slice(0, 10)
      : new Date().toISOString().slice(0, 10),
  });
  const [loading, setLoading] = useState(false);

  const onChange = (e) => setForm({ ...form, [e.target.id]: e.target.value });

  const submit = async () => {
    setLoading(true);
    try {
      await editExpense(item.id, {
        description: form.description,
        amount: form.amount,
        paidBy: form.paidBy,
        splitWith: form.splitWith,
        category: form.category,
        date: form.date,
      });
      onClose();
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal">
      <div className="modal-card glass">
        <h3>Edit Expense</h3>
        <div className="grid2">
          <input
            id="description"
            value={form.description}
            onChange={onChange}
          />
          <input
            id="amount"
            type="number"
            value={form.amount}
            onChange={onChange}
          />
          <input id="paidBy" value={form.paidBy} onChange={onChange} />
          <input id="splitWith" value={form.splitWith} onChange={onChange} />
          <select id="category" value={form.category} onChange={onChange}>
            {cats.map((c) => (
              <option key={c.name} value={c.name}>
                {c.name}
              </option>
            ))}
          </select>
          <input id="date" type="date" value={form.date} onChange={onChange} />
        </div>
        <div className="modal-actions">
          <button className="btn" onClick={submit} disabled={loading}>
            {loading ? "Saving…" : "Save"}
          </button>
          <button className="btn outline" onClick={onClose}>
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
}
