import React, { useMemo, useState } from "react";
import { useSelector } from "react-redux";
import AddExpense from "./popups/addExpense";
import "../../styles/dashboard.css";

/**
 * Beginner notes:
 * - We read the user from Redux with useSelector.
 * - We guard arrays so .map() never crashes.
 * - We sort expenses by date (newest first).
 * - We offer a search box and an "Add Expense" button (opens your popup).
 * - Totals compute safely (ignore bad/empty amounts).
 */

function asArray(v) {
  return Array.isArray(v) ? v : [];
}

function toNumber(v) {
  const n = parseFloat(v);
  return isNaN(n) ? 0 : n;
}

function formatDate(iso) {
  if (!iso) return "窶能

2-digit",
      month: "short",
      year: "numeric",
    });
  } catch {
    return iso;
  }
}

function ExpenseRow({ item }) {
  // Compatible with many shapes: {description, amount, date, user?}
  const desc =
    (item && (item.description || item.title || item.note)) || "Untitled";
  const amount = item ? toNumber(item.amount) : 0;
  const date = item && (item.date || item.createdAt || item.updatedAt);
  const who =
    (item && (item.user || item.friend || item.paidBy || item.with)) || null;

  return (
    <div className="exp-row">
      <div className="exp-main">
        <div className="exp-desc">{desc}</div>
        <div className="exp-meta">
          <span className="pill">{formatDate(date)}</span>
          {who ? (
            <span className="pill pill-ghost">@ {String(who)}</span>
          ) : null}
        </div>
      </div>
      <div className="exp-right">
        <div className="exp-amount">竄ｹ{amount.toFixed(2)}</div>
      </div>
    </div>
  );
}

export default function Dashboard() {
  // If your store is a single reducer, state.user is correct.
  // If you used combineReducers({ user: userReducer }), then read state.user.user instead.
  const userState = useSelector((state) => (state && state.user) || {});

  // When the reducer returns { user: doc }, we normalize:
  const userDoc =
    userState && userState._id !== undefined ? userState : userState.user || {};

  const username = userDoc && (userDoc.username || "User");
  const friends = asArray(userDoc && userDoc.friends);
  const expensesRaw = asArray(userDoc && userDoc.expensis);

  const [showAdd, setShowAdd] = useState(false);
  const [query, setQuery] = useState("");

  const expenses = useMemo(() => {
    const filtered = expensesRaw.filter((e) => {
      const desc = (e && (e.description || e.title || e.note)) || "";
      return desc.toLowerCase().includes(query.toLowerCase());
    });
    // Sort newest first by date/createdAt
    return filtered.slice().sort((a, b) => {
      const da = new Date(a && (a.date || a.createdAt || 0)).getTime();
      const db = new Date(b && (b.date || b.createdAt || 0)).getTime();
      return db - da;
    });
  }, [expensesRaw, query]);

  const totals = useMemo(() => {
    const total = expensesRaw.reduce((s, e) => s + toNumber(e && e.amount), 0);
    // We don't have owed/owes logic from backend; show "窶能
