import React, { useState } from "react";
import DashHeader from "../components/DashHeader";
import MiddleDashboard from "../components/Dashboard/MiddleDashboard";
import FriendList from "../components/Dashboard/FriendList";
import AddFriends from "../components/Dashboard/AddFriends";
import "../styles/dashboard.css";

export default function Dashboard() {
  const [showAddFriend, setShowAddFriend] = useState(false);
  const [showAddExpense, setShowAddExpense] = useState(false);
  const [showSettle, setShowSettle] = useState(false);

  return (
    <div className="dash">
      <div className="dash-gradient-bg" />
      <DashHeader
        onAddFriend={() => setShowAddFriend(true)}
        onAddExpense={() => setShowAddExpense(true)}
        onSettle={() => setShowSettle(true)}
      />
      <div className="dash-body">
        <FriendList />
        <MiddleDashboard
          externalShowAdd={showAddExpense}
          externalShowSettle={showSettle}
          onCloseAdd={() => setShowAddExpense(false)}
          onCloseSettle={() => setShowSettle(false)}
          onOpenAdd={() => setShowAddExpense(true)}
          onOpenSettle={() => setShowSettle(true)}
        />
      </div>
      {showAddFriend && <AddFriends onClose={() => setShowAddFriend(false)} />}
    </div>
  );
}
