import React, { useState } from "react";
import "../styles/signup.css";
import Header from "./Header";
import instance from "../utils/AxiosConfig";
import { useDispatch } from "react-redux";
import { loginSuccess } from "../redux/actionCreator/userAction";

export default function SignUp({ history }) {
  const [form, setForm] = useState({ username: "", email: "", password: "" });
  const [showPwd, setShowPwd] = useState(false);
  const [err, setErr] = useState("");
  const dispatch = useDispatch();

  const onChange = (e) => setForm({ ...form, [e.target.id]: e.target.value });

  const onSignup = async () => {
    setErr("");
    try {
      const payload = {
        username: form.username,
        email: String(form.email).trim(), // UI normalizing is ok; server also normalizes
        password: form.password,
      };
      const { data } = await instance.post("/user/signup", payload);
      localStorage.setItem("token", data.token);
      localStorage.setItem("user", JSON.stringify(data.user));
      dispatch(loginSuccess(data));
      history.push("/dashboard");
    } catch (e) {
      const serverMsg = e?.response?.data?.msg;
      console.error(
        "Signup error:",
        e?.response?.status,
        serverMsg || e.message
      );
      setErr(serverMsg || e.message || "Sign up failed");
    }
  };

  // quick sanity check of the API base
  // console.log('API base:', instance.defaults.baseURL);

  return (
    <div>
      <Header />
      <div className="container signup">
        <div className="signup-logo">
          <img
            src={require("../images/logo.png")}
            alt=""
            style={{ width: 160, objectFit: "contain" }}
          />
        </div>
        <div className="signup-form">
          <h3>INTRODUCE YOURSELF</h3>
          <label>Hi there! My name is</label>
          <input
            id="username"
            onChange={onChange}
            className="form-control"
            type="text"
            required
          />

          <label>Here’s my email address:</label>
          <input
            id="email"
            onChange={onChange}
            className="form-control"
            type="email"
            required
          />

          <label>And here’s my password:</label>
          <div className="password-field">
            <input
              id="password"
              onChange={onChange}
              className="form-control"
              type={showPwd ? "text" : "password"}
              required
            />
            <button className="show-btn" onClick={() => setShowPwd((s) => !s)}>
              {showPwd ? "Hide" : "Show"}
            </button>
          </div>

          {err && <p style={{ color: "red", marginTop: 8 }}>{err}</p>}
          <div className="btn-row">
            <button className="btn" onClick={onSignup}>
              Sign Up
            </button>
            <a className="btn btn-outline" href="/login">
              Log In
            </a>
          </div>
        </div>
      </div>
    </div>
  );
}
