// client/src/components/signup.js
import React, { useState } from "react";
import "../styles/signup.css";
import { withRouter, Link } from "react-router-dom";
import { instance } from "../utils/AxiosConfig";
import PasswordField from "./PasswordField";

const SignUp = (props) => {
  const [form, setForm] = useState({ username: "", email: "", password: "" });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { id, value } = e.target;
    setForm((prev) => ({ ...prev, [id]: value }));
  };

  const handleSubmit = async () => {
    if (!form.username || !form.email || !form.password) {
      alert("Form is incomplete");
      return;
    }
    try {
      setLoading(true);
      const res = await instance.post("/signup", form);
      const status = res?.data?.Status;

      if (status === "S") {
        alert("Successfully Registered");
        props.history.push("/Dashboard");
      } else if (status === "F") {
        alert("Username or Email already exists");
      } else {
        alert("Unexpected server response");
      }
    } catch (err) {
      console.error(err);
      alert("Something went wrong. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const goToLogin = () => props.history.push("/login");

  return (
    <div className="container signup">
      <div className="signup-logo">
        <img src={require("../images/logo.png")} alt="Logo" />
      </div>

      <div className="signup-form">
        <h3>INTRODUCE YOURSELF</h3>

        <label htmlFor="username">Hi there! My name is</label>
        <input
          id="username"
          className="form-control"
          type="text"
          required
          value={form.username}
          onChange={handleChange}
          autoComplete="username"
        />

        <label htmlFor="email">Here’s my email address:</label>
        <input
          id="email"
          className="form-control"
          type="email"
          required
          value={form.email}
          onChange={handleChange}
          autoComplete="email"
        />

        <label htmlFor="password">And here’s my password:</label>
        <PasswordField
          id="password"
          value={form.password}
          onChange={handleChange}
          placeholder="Create a password"
        />

        <div style={{ display: "flex", gap: 10, marginTop: 12 }}>
          <button
            onClick={handleSubmit}
            className="btn"
            disabled={loading}
            type="button"
          >
            {loading ? "Signing up…" : "Sign me up!"}
          </button>
          <button
            onClick={goToLogin}
            className="btn btn-secondary"
            type="button"
          >
            Go to Login
          </button>
        </div>

        <p className="helper" style={{ marginTop: 10 }}>
          Already have an account? <Link to="/login">Log in</Link>
        </p>
      </div>
    </div>
  );
};

export default withRouter(SignUp);
