import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import FriendList from "../components/Dashboard/FriendList";
import MiddleDashboard from "../components/Dashboard/MiddleDashboard";
import "../styles/dashboard.css";
import { logout } from "../redux/actionCreator/userAction";
import { getInitialTheme, toggleTheme } from "../utils/theme";

export default function DashboardPage({ history }) {
  const dispatch = useDispatch();
  const me = useSelector((s) => s.me.me);

  // quick action modals controlled from this top bar
  const [openAdd, setOpenAdd] = useState(false);
  const [openSettle, setOpenSettle] = useState(false);

  // theme toggle state (for icon only)
  const [theme, setTheme] = useState(getInitialTheme());

  const onLogout = () => {
    // clear auth and go to login
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    dispatch(logout());
    history.push("/login"); // react-router v5
  };

  const onToggleTheme = () => setTheme(toggleTheme());

  return (
    <div className="dashboard-wrap">
      {/* Top bar (search + actions) */}
      <div
        className="topbar glass"
        style={{
          display: "flex",
          alignItems: "center",
          gap: 12,
          padding: "10px 14px",
          margin: "10px 18px 6px",
          borderRadius: 16,
        }}
      >
        <div style={{ flex: 1 }}>
          <input
            className="search"
            placeholder="  Search expenses, notes, friends…"
            style={{
              width: "100%",
              padding: "12px 14px",
              borderRadius: 14,
              border: "1px solid var(--border)",
              background: "rgba(255,255,255,.06)",
              color: "var(--text)",
              outline: "none",
            }}
          />
        </div>

        <div style={{ display: "flex", gap: 10, alignItems: "center" }}>
          <button
            className="btn ghost"
            onClick={() => {
              /* hook your add friend modal here if you want */
            }}
          >
            + Friend
          </button>
          <button className="btn" onClick={() => setOpenAdd(true)}>
            + Expense
          </button>
          <button className="btn outline" onClick={() => setOpenSettle(true)}>
            Settle
          </button>

          {/* Theme toggle (persists to localStorage) */}
          <button
            className="btn ghost"
            onClick={onToggleTheme}
            title="Toggle dark / light"
            style={{ width: 42, padding: "10px 0" }}
          >
            {theme === "dark" ? "☀️" : "🌙"}
          </button>

          {/* User bubble */}
          <div
            title={me?.email || ""}
            style={{
              width: 36,
              height: 36,
              borderRadius: "50%",
              display: "grid",
              placeItems: "center",
              background: "rgba(255,255,255,.12)",
              border: "1px solid var(--border)",
              color: "var(--text)",
              fontWeight: 700,
            }}
          >
            {(me?.username || me?.email || "U").trim().charAt(0).toUpperCase()}
          </div>

          {/* Logout */}
          <button
            className="btn"
            style={{ background: "linear-gradient(135deg,#ff5c7c,#ff7f99)" }}
            onClick={onLogout}
          >
            Logout
          </button>
        </div>
      </div>

      {/* Main layout */}
      <div
        className="dash-grid"
        style={{
          display: "grid",
          gridTemplateColumns: "320px 1fr",
          gap: 16,
          padding: "6px 18px 18px",
        }}
      >
        <FriendList />
        <MiddleDashboard
          externalShowAdd={openAdd}
          externalShowSettle={openSettle}
          onOpenAdd={() => setOpenAdd(true)}
          onOpenSettle={() => setOpenSettle(true)}
          onCloseAdd={() => setOpenAdd(false)}
          onCloseSettle={() => setOpenSettle(false)}
        />
      </div>
    </div>
  );
}
