import React, { useEffect, useMemo, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import AddExpense from "./popups/addExpense";
import SettleUp from "./popups/settleUp";
import CategoryChart from "./CategoryChart";
import {
  computeFromExpenses,
  formatCurrency,
  timeAgo,
} from "../../utils/finance";
import { loadMe, deleteExpense } from "../../data/meApi";
import "../../styles/dashboard.css";
import EditExpense from "./popups/EditExpense";
import Confirm from "./popups/Confirm";
import { toCSV, parseCSV } from "../../utils/csv";
import { pushToast } from "../../redux/reducers/uiReducer";

export default function MiddleDashboard({
  externalShowAdd,
  externalShowSettle,
  onCloseAdd,
  onCloseSettle,
  onOpenAdd,
  onOpenSettle,
}) {
  const dispatch = useDispatch();
  const { loading, me, expenses, categories } = useSelector((s) => s.me);
  const [showAdd, setShowAdd] = useState(false);
  const [showSettle, setShowSettle] = useState(false);
  const [editItem, setEditItem] = useState(null);
  const [confirmDel, setConfirmDel] = useState(null);
  const [month, setMonth] = useState(new Date().getMonth());
  const [catFilter, setCatFilter] = useState("");

  useEffect(() => setShowAdd(!!externalShowAdd), [externalShowAdd]);
  useEffect(() => setShowSettle(!!externalShowSettle), [externalShowSettle]);

  useEffect(() => {
    dispatch(loadMe());
  }, [dispatch]);

  const stats = useMemo(
    () =>
      computeFromExpenses(me?.email, expenses, {
        monthFilter: month,
        categoryFilter: catFilter || null,
      }),
    [me, expenses, month, catFilter]
  );
  const { totals, recent, chart } = stats;

  const openAdd = () => {
    setShowAdd(true);
    onOpenAdd && onOpenAdd();
  };
  const openSettle = () => {
    setShowSettle(true);
    onOpenSettle && onOpenSettle();
  };
  const closeAdd = () => {
    setShowAdd(false);
    onCloseAdd && onCloseAdd();
    dispatch(loadMe());
  };
  const closeSettle = () => {
    setShowSettle(false);
    onCloseSettle && onCloseSettle();
    dispatch(loadMe());
  };

  const exportCSV = () => {
    const csv = toCSV(expenses);
    const blob = new Blob([csv], { type: "text/csv;charset=utf-8;" });
    const a = document.createElement("a");
    a.href = URL.createObjectURL(blob);
    a.download = "expenses.csv";
    a.click();
  };

  const importCSV = async (e) => {
    const file = e.target.files?.[0];
    if (!file) return;
    const text = await file.text();
    const { rows } = parseCSV(text);
    if (!rows.length) {
      dispatch(pushToast("Empty CSV", "error"));
      return;
    }
    // naive import: push each as new expense (client-side); better: server bulk endpoint
    for (const r of rows) {
      // required: description, amount, paidBy
      if (!r.description || !r.amount || !r.paidBy) continue;
      await fetchImportRow(r);
    }
    dispatch(loadMe());
    dispatch(pushToast("CSV imported", "success"));
  };

  const fetchImportRow = async (r) => {
    try {
      await fetch(
        `${
          window._apiBase ||
          process.env.REACT_APP_API_URL ||
          "http://localhost:3001/api"
        }/user/expense/add`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: "Bearer " + localStorage.getItem("token"),
          },
          body: JSON.stringify({
            description: r.description,
            amount: r.amount,
            paidBy: r.paidBy,
            paidTo: r.paidTo || "",
            splitWith: r.splitWith || "",
            category: r.category || "Other",
            date: r.date || new Date().toISOString(),
          }),
        }
      );
    } catch {}
  };

  const months = Array.from({ length: 12 }, (_, i) => i);

  return (
    <main className="middle">
      {/* Filters + tools */}
      <section
        className="quick-actions"
        style={{ justifyContent: "space-between", flexWrap: "wrap" }}
      >
        <div style={{ display: "flex", gap: 8, alignItems: "center" }}>
          <select
            value={month}
            onChange={(e) => setMonth(Number(e.target.value))}
          >
            {months.map((m) => (
              <option key={m} value={m}>
                {new Date(2000, m, 1).toLocaleString("en-US", {
                  month: "long",
                })}
              </option>
            ))}
          </select>
          <select
            value={catFilter}
            onChange={(e) => setCatFilter(e.target.value)}
          >
            <option value="">All Categories</option>
            {categories.map((c) => (
              <option key={c.name} value={c.name}>
                {c.name}
              </option>
            ))}
          </select>
        </div>
        <div style={{ display: "flex", gap: 8 }}>
          <button className="btn" onClick={openAdd}>
            + Add Expense
          </button>
          <button className="btn outline" onClick={openSettle}>
            Settle Up
          </button>
          <button className="btn ghost" onClick={exportCSV}>
            Export CSV
          </button>
          <label className="btn outline" style={{ cursor: "pointer" }}>
            Import CSV
            <input
              type="file"
              accept=".csv"
              onChange={importCSV}
              style={{ display: "none" }}
            />
          </label>
        </div>
      </section>

      {/* Stat cards */}
      <section className="cards">
        <div className="card glass">
          <div className="card-top">
            <span className="chip green">Total Balance</span>
          </div>
          <div className={`big ${loading ? "skel" : ""}`}>
            {formatCurrency(totals.totalBalance)}
          </div>
          <div className="mini">{loading ? "Loading…" : "Net of owe/owed"}</div>
        </div>
        <div className="card glass">
          <div className="card-top">
            <span className="chip red">You Owe</span>
          </div>
          <div className={`big ${loading ? "skel" : ""}`}>
            {formatCurrency(totals.youOwe)}
          </div>
          <div className="mini">
            {loading ? "Loading…" : "What you need to pay"}
          </div>
        </div>
        <div className="card glass">
          <div className="card-top">
            <span className="chip blue">Owed to You</span>
          </div>
          <div className={`big ${loading ? "skel" : ""}`}>
            {formatCurrency(totals.owedToMe)}
          </div>
          <div className="mini">
            {loading ? "Loading…" : "What others owe you"}
          </div>
        </div>
        <div className="card glass wide">
          <div className="card-top">
            <span className="chip">Monthly Spend</span>
            <span className="mini">
              {new Date(2000, month, 1).toLocaleString("en-US", {
                month: "long",
              })}
            </span>
          </div>
          <div className="progress">
            <div
              className="bar"
              style={{
                width:
                  (chart.total
                    ? Math.min(100, (chart.total / (chart.total * 1.2)) * 100)
                    : 0) + "%",
              }}
            />
          </div>
          <div className="mini">{formatCurrency(chart.total)} (your share)</div>
        </div>
      </section>

      {/* Panels: Recent + Chart */}
      <section className="panels">
        <div className="panel glass">
          <div className="panel-head">
            <h3>Recent Activity</h3>
            <button className="link" onClick={() => dispatch(loadMe())}>
              Refresh
            </button>
          </div>
          {loading ? (
            <div className="skel" style={{ height: 120 }} />
          ) : recent.length === 0 ? (
            <div className="mini">No activity for filters.</div>
          ) : (
            <ul className="activity">
              {recent.map((r) => (
                <li key={r.id}>
                  <span className="dot blue" />
                  <span>
                    <b>{r.description}</b> — {formatCurrency(r.amount)}
                  </span>
                  <span className="mini" style={{ marginLeft: 8 }}>
                    Paid by {r.paidBy} • {r.category}
                  </span>
                  <span className="time">{timeAgo(r.date)}</span>
                  <div style={{ marginLeft: 8, display: "flex", gap: 6 }}>
                    <button
                      className="btn ghost"
                      onClick={() => setEditItem(r)}
                    >
                      Edit
                    </button>
                    <button
                      className="btn outline"
                      onClick={() => setConfirmDel(r)}
                    >
                      Delete
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          )}
        </div>
        <CategoryChart data={chart.data} total={chart.total} />
      </section>

      {showAdd && <AddExpense onClose={closeAdd} />}
      {showSettle && <SettleUp onClose={closeSettle} />}
      {editItem && (
        <EditExpense item={editItem} onClose={() => setEditItem(null)} />
      )}
      {confirmDel && (
        <Confirm
          title="Delete expense?"
          subtitle={confirmDel.description}
          onCancel={() => setConfirmDel(null)}
          onConfirm={async () => {
            await deleteExpense(confirmDel.id);
            setConfirmDel(null);
          }}
        />
      )}
    </main>
  );
}
